import java.util.*;
public class 정수입력후출력 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc = new Scanner(System.in);
		
		int Size = sc.nextInt();
		for(int i=0;i<Size;i++) {
			int A=sc.nextInt();
			int B=sc.nextInt();
			System.out.println(A+B);
		}
			
	}

}
