import java.util.*;
public class 코딩은체육과목입니다_25314_반복문 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc = new Scanner(System.in);
		// N 은 4의 배수이다.
		int N = sc.nextInt();
		// 4는 long 으로 
		
		for(int i=0;i<N/4;i++) {
			System.out.print("long"+" ");
		}
		System.out.print("int");
		
	}

}
